        else {\r\n                    document.getElementById(\"erro-atividade2\").style.display = \"none\";\r\n                    document.getElementById(\"acerto-atividade2\").style.display =  \"block\";\r\n                }\r\n            }\r\n        }\r\n        function atividade_3() {\r\n            let resp3;\r\n            let temp = document.getElementsByName(\"opt3\");\r\n            for (var i = 0; i < temp.length; i++) {\r\n                if (temp[i].checked) resp3 = temp[i].value;\r\n            }\r\n            if (resp3 === \"1\") {\r\n                document.getElementById(\"erro-atividade3\").innerHTML = \"Se o len\u00E7o custa 1, a camisa custa 101. A soma dos dois itens d\u00E1 102, e n\u00E3o 110. Vamos tentar de novo?\";\r\n                document.getElementById(\"erro-atividade3\").style.display = \"block\";\r\n                document.getElementById(\"acerto-atividade3\").style.display = \"none\";                \r\n            }\r\n            else if (resp3 === \"2\") {\r\n                document.getElementById(\"erro-atividade3\").innerHTML = \"\";\r\n                document.getElementById(\"erro-atividade3\").style.display = \"none\";\r\n                document.getElementById(\"acerto-atividade3\").style.display = \"block\";                \r\n            }\r\n            else if (resp3 === \"3\") {\r\n                document.getElementById(\"erro-atividade3\").innerHTML = \"O sistema autom\u00E1tico enganou voc\u00EA. Ele buscou a coer\u00EAncia nos n\u00FAmeros, subtraindo 10 de 110. Note que, se o len\u00E7o custa 10, a camisa custa 110. A soma dos dois itens d\u00E1 120, e n\u00E3o 110. Quer tentar de novo?\";\r\n                document.getElementById(\"erro-atividade3\").style.display = \"block\";\r\n                document.getElementById(\"acerto-atividade3\").style.display = \"none\";                \r\n            }\r\n            else if (resp3 === \"4\") {\r\n                document.getElementById(\"erro-atividade3\")